={{ (() => {
  // ===== Inputs uit subflow =====
  const fromISO = $json.fromISO || null;
  const toISO   = $json.toISO   || null;
  const qRaw    = String($json.query ?? '').replace(/"/g, '\\"'); // escape quotes
  const topK    = Number($json.topK ?? 50); // wat ruimer bij hybrid
  const alpha   = Number($json.alpha ?? 0.6); // 0=dense-only, 1=sparse-only

  // ===== WHERE-builder =====
  function buildWhere(fromISO, toISO) {
    const ops = [];
    if (fromISO) ops.push(`{ path: ["publishedAt"], operator: GreaterThanEqual, valueDate: "${fromISO}" }`);
    if (toISO)   ops.push(`{ path: ["publishedAt"], operator: LessThan,        valueDate: "${toISO}" }`);
    return ops.length ? `
      where: {
        operator: And
        operands: [ ${ops.join(', ')} ]
      }` : '';
  }
  const whereClause = buildWhere(fromISO, toISO);

  // ===== Vector voorbereiden (optioneel) =====
  const raw = Array.isArray($json.vector) ? $json.vector : [];
  const vecArr = raw.filter(v => Number.isFinite(Number(v)));
  const vector = vecArr.length ? vecArr.map(v => Number(v).toFixed(8)).join(',') : '';
  const vectorPart = vector ? `vector: [${vector}]` : ''; // optioneel

  // ===== HYBRID-query =====
  return `
  {
    Get {
      Article(
        hybrid: {
          query: "${qRaw}"
          alpha: ${alpha}
          ${vectorPart}
          targetVectors: ["te_3_large"]
          properties: ["title","lead","body"]
        }
        ${whereClause}
        limit: ${topK}
      ) {
        rdId
        publishedAt
        title
        lead
        _additional { id score distance }
      }
    }
  }`;
})() }}

