// === Format Output — strikte tijdsfilter + robuuste linkconstructie ===

// 1️⃣ Ophalen & sanity-check
const raw = $json?.data?.Get?.Article ?? [];
if (!Array.isArray(raw) || !raw.length) {
  return { results: [], note: 'Geen artikelen gevonden' };
}

// 2️⃣ Parameters
const fromISO = $json.fromISO ?? null;
const toISO   = $json.toISO   ?? null;
const hasRange = !!(fromISO || toISO);

const THRESH = hasRange ? 0.9 : 0.7;

const fromMs = fromISO ? new Date(fromISO).getTime() : -Infinity;
const toMs   = toISO   ? new Date(toISO).getTime()   :  Infinity;

// 3️⃣ Filter op distance (relevantie)
const prelim = raw.filter(o => (o._additional?.distance ?? 1) < THRESH);
const used0  = prelim.length ? prelim : raw;

// 4️⃣ Filter op tijdsrange (defensief en strikt)
const used1 = used0.filter(o => {
  const t = new Date(o.publishedAt).getTime();
  return Number.isFinite(t) && t >= fromMs && t < toMs;
});

// 5️⃣ Alleen in-range meenemen als range opgegeven is
const used = hasRange ? used1 : used0;

// 6️⃣ Sorteer nieuw → oud
used.sort((a, b) => (new Date(b.publishedAt).getTime() || 0) - (new Date(a.publishedAt).getTime() || 0));

// 7️⃣ Helper: robuuste RD-link
function buildRdLink(o) {
  const base = 'https://www.rd.nl';
  const norm = s => s.replace(/\/+/g, '/').replace(':/', '://');

  if (o.url && /^https?:\/\//i.test(o.url)) return o.url; // directe URL
  if (o.slug && typeof o.slug === 'string') {
    const s = o.slug.replace(/^\/+/, '');
    if (/^artikel\//i.test(s)) return `${base}/${norm(s)}`;
    return `${base}/${norm('artikel/' + s)}`;
  }
  if (o.rdId) return `${base}/${norm('artikel/' + String(o.rdId))}`;
  return null;
}

// 8️⃣ Hulpfunctie om NL-datum te bekijken
function toAmsISO(isoStr) {
  if (!isoStr) return null;
  try {
    const ams = new Date(new Date(isoStr).toLocaleString('en-US', { timeZone: 'Europe/Amsterdam' }));
    return ams.toISOString();
  } catch {
    return null;
  }
}

// 9️⃣ Map resultaat
const results = used.map(o => {
  const pub = new Date(o.publishedAt);
  const pubMs = pub.getTime();
  const inRange = hasRange ? (pubMs >= fromMs && pubMs < toMs) : true;

  return {
    rdId: o.rdId ?? null,
    title: o.title,
    publishedAt: o.publishedAt,
    publishedAt_ams: toAmsISO(o.publishedAt),
    lead: o.lead,
    body: o.body,
    distance: o._additional?.distance ?? null,
    link: buildRdLink(o),
    in_range: inRange
  };
});

// 10️⃣ Teruggeven
return {
  results,
  count: results.length,
  distance_threshold: THRESH,
  applied_fromISO: fromISO,
  applied_toISO: toISO,
  strict_range: hasRange
};

