=={{ (() => {
  const raw = String($json.query ?? '').trim().replace(/\\s+/g,' ');
  const q    = raw.length ? raw : 'algemeen onderwerp';
  const qSafe = q.replace(/\"/g, '\\\"');

  const fromISO = $json.fromISO ?? null;
  const toISO   = $json.toISO   ?? null;
  const forceNewest = ($json.forceNewest === true) || String($json.forceNewest).toLowerCase() === 'true';

  let tijd = '';
  if (fromISO && toISO) tijd = `verschenen tussen ${fromISO} en ${toISO} (einddatum exclusief)`;
  else if (fromISO)      tijd = `verschenen op of na ${fromISO}`;
  else if (toISO)        tijd = `verschenen vóór ${toISO}`;
  else if (forceNewest)  tijd = `met nadruk op de meest recent verschenen artikelen`;

  const tijdZin = tijd ? ` Beperk tot artikelen ${tijd}.` : '';

  return `Artikelen over "${qSafe}" uit het archief van Reformatorisch Dagblad (Erdee Media Groep). Focus op nieuws, achtergronden en analyses die inhoudelijk sterk overeenkomen met het onderwerp.${tijdZin} Gebruik titel en hoofdtekst als semantische context.`;
})() }}

