// publication-date encoding — Run Once for Each Item

function getAgeVector(publishedAtISO, ageWeight = 1.0) {
  const oneDayMs = 86_400_000;
  const referenceDate = new Date(Date.UTC(2023, 7, 10)); // 10 Aug 2023 UTC

  const publishedAt = new Date(publishedAtISO);
  if (isNaN(publishedAt.getTime())) return { vec: [0.0, 0.0], anchorUsed: null };

  const halfSpanDays = 5 * 365;
  const minDate = new Date(referenceDate.getTime() - halfSpanDays * oneDayMs);
  const maxDate = new Date(referenceDate.getTime() + halfSpanDays * oneDayMs);

  const matchDate = publishedAt;
  if (matchDate < minDate) return { vec: [0.0, -1.0], anchorUsed: matchDate.toISOString() };
  if (matchDate > maxDate) return { vec: [0.0,  1.0], anchorUsed: matchDate.toISOString() };

  const diffDaysInt = Math.floor((matchDate.getTime() - referenceDate.getTime()) / oneDayMs);
  const theta = (diffDaysInt * Math.PI) / (10 * 365);

  const w = Number(ageWeight);
  const x = Number(Math.cos(theta).toFixed(8)) * w;
  const y = Number(Math.sin(theta).toFixed(8)) * w;

  return { vec: [x, y], anchorUsed: matchDate.toISOString() };
}

function pickAnchorISO(fromISO, toISO) {
  const oneDayMs = 86_400_000;

  const from = fromISO ? new Date(fromISO) : null;
  const to   = toISO   ? new Date(toISO)   : null;

  const fromOk = !!from && !Number.isNaN(from.getTime());
  const toOk   = !!to   && !Number.isNaN(to.getTime());

  if (fromOk && toOk) {
    // midden van het venster
    return new Date((from.getTime() + to.getTime()) / 2).toISOString();
  }
  if (toOk) {
    // exclusieve bovenkant → één dag terug
    return new Date(to.getTime() - oneDayMs).toISOString();
  }
  if (fromOk) {
    // alleen from: anker = from
    return new Date(from.getTime()).toISOString();
  }
  // fallback: nu
  return new Date().toISOString();
}


const baseVector = Array.isArray($json.vector) ? $json.vector : [];
const useAge     = ($json.use_age ?? true) === true;
const ageWeight  = Number($json.age_weight ?? 2.0);

const fromISO = $json.fromISO ?? null;
const toISO   = $json.toISO   ?? null;

const anchorISO = pickAnchorISO(fromISO, toISO);
const age = useAge ? getAgeVector(anchorISO, ageWeight) : { vec: [0.0, 0.0], anchorUsed: null };

const finalVector = baseVector.concat(age.vec);

return {
  vector: finalVector,
  topK: Number($json.topK ?? 3),
  age_anchor_used: age.anchorUsed ?? anchorISO,
  age_weight_used: ageWeight,
  fromISO,
  toISO,
  anchorISO,
  _debug: { fromISO, toISO, anchorISO, baseLen: baseVector.length, finalLen: finalVector.length }
};
